export const onResize = e => { e.invokeMethodAsync("OnResizeNotifier", { windowDimensions: getWindowDimensions(), screen: getScreen() }) }; export const onOrientationChange = e => { e.invokeMethodAsync("OnOrientationChangeNotifier", { windowDimensions: getWindowDimensions(), screen: getScreen() }) }; export const registerEventHandlers = e => { window.addEventListener("resize", (() => onResize(e))), window.screen.orientation.addEventListener("change", (() => onOrientationChange(e))) }; export const alert = e => window.alert(e); export const prompt = e => window.prompt(e); export const focus = () => { window.focus() }; export const blur = () => { window.blur() }; export const print = () => { window.print() }; export const logToConsole = e => { console.log(e) }; export const clearConsole = () => { console.clear() }; export const startConsoleTimer = (e = "default") => { console.time(e) }; export const logConsoleTimer = (e = "default") => { console.timeLog(e) }; export const stopConsoleTimer = (e = "default") => { console.timeEnd(e) }; export const matchesMedia = e => window.matchMedia(e).matches; export const scrollTo = (e, n) => { window.scrollTo(e, n) }; export const getScreen = () => ({ width: window.screen.width, height: window.screen.height, orientation: { type: window.screen.orientation.type, angle: window.screen.orientation.angle } }); export const getWindowDimensions = () => ({ innerWidth: window.innerWidth, innerHeight: window.innerHeight, outerWidth: window.outerWidth, outerHeight: window.outerHeight }); export const getScreenHeight = () => window.screen.height; export const getScreenWidth = () => window.screen.width; export const getScreenOrientation = () => window.screen.orientation.type;